import React, { Component } from 'react'
import NewsItem from './NewsItem'
import PropTypes from 'prop-types'


export class News extends Component {
    static defaultProps = {
        country: "in"
    }
    
    static PropTypes = {
        country: PropTypes.string
    }
    constructor() {
        super()
        console.log("Hello I am a constructor from News component ");
        this.state = {
            articles: this.articles,
            loading: false,
            page: 1
        }
    }

    async componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=b819defa3f7f4b38b2d93c371a0707cd&page=1&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({ articles: parsedData.articles, totalResults: parsedData.totalResults })

    }
    handlePrevClick = async () => {
        console.log("This is Prev function");
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=b819defa3f7f4b38b2d93c371a0707cd&page= ${this.state.page - 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles
        })
    }
    handleNextClick = async () => {
        if (this.state.page + 1 > Math.ceil(this.setState.totalResults / 20)) {

        }
        else {
            console.log("This is Next function");
            let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=b819defa3f7f4b38b2d93c371a0707cd&page= ${this.state.page + 1}&pageSize=20`;
            let data = await fetch(url);
            let parsedData = await data.json()
            console.log(parsedData);
            this.setState({
                page: this.state.page + 1,
                articles: parsedData.articles
            })
        }
    }
    render() {
        return (
            <div className="container my-3">
                <h2>Welcome to NewsMonkey </h2>
                <div className="row" >
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title ? element.title.slice(0, 39) : ""} description={element.description ? element.description.slice(0, 105) : ""} imageUrl={element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}

                </div>
                <div className="container d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}>&larr; Previous </button>
                    <button disabled={this.state.page >= 4} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
                </div>
            </div>
        )
    }
}

export default News;
